import React from 'react';
import './App.css';
import 'antd/dist/antd.css'

import { Input, Tooltip, Icon } from 'antd';

class LoginForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            Login: '',
            Senha: ''
        };

        this.handleChangeLogin = this
            .handleChangeLogin
            .bind(this);
        this.handleChangeSenha = this
            .handleChangeSenha
            .bind(this);

        this.handleSubmit = this
            .handleSubmit
            .bind(this);
    }

    handleChangeLogin(event) {
        this.setState({Login: event.target.value});
    }
    handleChangeSenha(event) {
      this.setState({Senha: event.target.value});
    }

    handleSubmit(event) {
        alert('Login: ' + this.state.Login + " Senha: " + this.state.Senha);
        event.preventDefault();
    }

    render() {
        return (
          <header className="App-header">
            <h1>
              Routine App
            </h1>
            <form onSubmit={this.handleSubmit}>
                <label className="Login">
                    Nome:
                    <input type="text" value={this.state.Login} onChange={this.handleChangeLogin}/>
                </label>
                <label className="Senha">
                    Senha:
                    <input type="text" value={this.state.Senha} onChange={this.handleChangeSenha}/>
                </label>
                <input className= "submitbutton" type="submit" value="Enviar"/>

            </form>
          </header>
        );
    }
}

export default LoginForm;
