import React, { Component } from 'react';
import { render } from 'react-dom';

import 'antd/es/date-picker/style/css';
import InputLabel from '../../components/InputLabel.js'
import Lista from '../../components/Lista.js'


import {listarObjetos} from '../../firebase/objetos.js';

class Visualizar extends Component {

  constructor(props){
    super(props)

    this.state = {
      obj:  [
        {
          nome: 'item1',
          local: 'local1'
        },
        {
          nome: 'item2',
          local: 'local2'
        },
        {
          nome: 'item2',
          local: 'local2'
        },
        {
          nome: 'item2',
          local: 'local2'
        },
        {
          nome: 'item2',
          local: 'local2'
        },
        {
          nome: 'item2',
          local: 'local2'
        }
      ]
    }

    this.adicionarObjeto = this.adicionarObjeto.bind(this)

    listarObjetos(() => {
      this.setState({obj: []})
    }, (data) => {
      this.adicionarObjeto({
        nome: data.nome,
        local: data.local
      })
    })
  }

  adicionarObjeto(obj){
    this.setState({obj: (this.state.obj.concat([obj]))})
  }

  render() {
    return (
      <div className = "Background">
          <Lista itens = {this.state.obj} tam = {this.state.obj.length}/>  
      </div>
    );
  }
}

export default Visualizar;
